package channelsimport kotlinx.coroutines.channels.Channelimport kotlinx.coroutines.launchimport kotlinx.coroutines.runBlockingfun main() {    runBlocking {        val channels = Channel<Int>()        launch {            for(x in 1..5){                channels.send(x*x)            }        }        for (i in 1..5){            println(channels.receive())        }    }    // another way    runBlocking {        val channels = Channel<Int>()        launch {            for(x in 1..5){                channels.send(x*x*x)                channels.close()            }        }        for (i in channels){            println(i)        }    }}