package contextimport kotlinx.coroutines.CoroutineNameimport kotlinx.coroutines.GlobalScopeimport kotlinx.coroutines.launchimport kotlinx.coroutines.runBlockingimport kotlin.coroutines.CoroutineContextfun main() {    runBlocking {        launch {          CoroutineName("MyCoroutines")          println("THis is run from ${coroutineContext[CoroutineName.Key]}")        }        GlobalScope.launch {            println("")        }    }}