package flowExampleimport kotlinx.coroutines.flow.collectimport kotlinx.coroutines.flow.flowimport kotlinx.coroutines.runBlockingfun main() {    println("Start of Main thread")    runBlocking {        sendNumbers().collect {            println("Received numnbers $it")        }        println("Finished Receiving numbers")    }    println("End of Main thread")}fun sendNumbers() = flow {    for(i in 1..10){        emit(i)    }}