package flowExampleimport kotlinx.coroutines.flow.Flowimport kotlinx.coroutines.flow.collectimport kotlinx.coroutines.flow.flowimport kotlinx.coroutines.runBlockingimport kotlinx.coroutines.withTimeoutOrNullfun main() {    runBlocking {        val numbersFlow = sendNumbersTOCheckCancellation()        println("FLow has not started yet")        println("Started Flow now")        withTimeoutOrNull(1000L){            numbersFlow.collect {                println(it)            }        }    }}fun sendNumbersTOCheckCancellation() : Flow<Int> = flow{    val list = listOf(1, 2, 3, 4, 5, 6)    list.forEach {        kotlinx.coroutines.delay(400L)        emit(it)    }}