package operatorsimport kotlinx.coroutines.delayimport kotlinx.coroutines.flow.asFlowimport kotlinx.coroutines.flow.collectimport kotlinx.coroutines.flow.filterimport kotlinx.coroutines.flow.mapimport kotlinx.coroutines.runBlockingfun main() {    runBlocking {        filterOperator1()    }}suspend fun filterOperator1(){    (1..10).asFlow()        .filter {            it%2 == 0        }        .collect {            println(it)        }}