package operatorsimport kotlinx.coroutines.delayimport kotlinx.coroutines.flow.asFlowimport kotlinx.coroutines.flow.collectimport kotlinx.coroutines.flow.mapimport kotlinx.coroutines.runBlockingfun main() {    runBlocking {        mapOperator1()    }}suspend fun mapOperator1(){    (1..10).asFlow()        .map {            delay(500L)            "Mapping $it"        }        .collect {            println(it)        }}