package scopeimport kotlinx.coroutines.CoroutineScopeimport kotlinx.coroutines.coroutineScopeimport kotlinx.coroutines.launchimport kotlinx.coroutines.runBlocking/** * coroutineScope is a block of code which will complete its execution * after executing it's child coroutines * For more details check the output of the below code */fun main() {    runBlocking {        coroutineScope {            println("Start of coroutines scope")            launch {                println("Running first coroutine")            }            launch {                println("Running Second coroutine")            }            println("End of coroutines scope")        }    }}