package scopeimport kotlinx.coroutines.GlobalScopeimport kotlinx.coroutines.delayimport kotlinx.coroutines.launch/** * GlobalScope.launch -  provides the lifecycle of the coroutines * its lifecycle is until the entire application * * If you execute launch inside GlobalScope.launch. it will execute * In this below code Coroutine executed inside launch will not print * * The output of the code is *  Main thread started    Main thread execution end    Global Scope executing */fun main() {    println("Main thread started")    GlobalScope.launch {        println("Global Scope executing")        launch {            delay(5000)            println("Coroutine executed inside launch")        }    }    println("Main thread execution end")}