package scopeimport kotlinx.coroutines.*fun main() {    println("Program execution now will block")    runBlocking {        launch {            delay(1000L)            println("Task from Run Blocking ")        }        GlobalScope.launch {            delay(1000L)            println("Task from global scope")        }        coroutineScope {            delay(1000L)            println("Task from coroutines scope")        }    }    println("Program execution now will atart")}