package suspendingFUnctionimport kotlinx.coroutines.GlobalScopeimport kotlinx.coroutines.delayimport kotlinx.coroutines.launchvar functionCalls = 0fun main() {    GlobalScope.launch {        completeFUnction()    }    GlobalScope.launch {        improveMessage()    }    print("Hello ")    Thread.sleep(2000L)    println("There have been $functionCalls cals so far" )}suspend fun completeFUnction(){    delay(500L)    println("World")    functionCalls++}suspend fun improveMessage(){    delay(1000L)    println("Suspend functions are cool")    functionCalls++}